INSERT INTO `basicinformationtemp` VALUES (72, NULL, 'Werner', 'Waldner', 'M2017-12168614219', NULL, '02-04-1958', NULL, NULL, 'Sport, Musik, Reisen', 'Werner Waldner', 'Mentor', 'werner.waldner@allianz.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'hochschulabschluss', 'Promotion', NULL, 'Schwimmen, Moutainbiken, Skifahren', NULL, NULL, 'seit vielen Jahren arbeite ich als MFC. die Arbeit mit Jugendlichen finde ich anregend und herausfordernd. ich habe selbst 2 Kinder (mittlerweile schon 22 J alt, Zwillinge). ich suche immer den offenen Austausch', 'uploads/2017101091.MOV', '20171219123710', '0', '80802 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Senior project manager', 'meine Erfahrung als langjaehriger Angestellter einzubringen aber auch herausgefordert zu werden', 'ueber MFC', 'Koeniginstr. 85', 'Germany (Deutschland)', '', 'Allianz SE', '80802 Nünchen', 'VWL ', NULL, '10-10-2017');
INSERT INTO `basicinformationtemp` VALUES (73, NULL, 'Karin', 'Beutlhauser', 'M2017-12178975919', NULL, '18-01-1984', NULL, NULL, 'Yoga, Bergsport, Skifahrn, Kochen, Theater und Musik', 'Karin Beutlhauser', 'Mentor', 'karin.beutlhauser@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Dipl. Betriebswirtin', 'Diplom', NULL, 'Yoga, Tennis, Reiten', NULL, NULL, '- Ich bin ein großer Fan der digitalen Welt und interessiere mich sehr für die offene Lebensweise der Jugendliche im Netz\n- Ich gebe gerne Wissen weiter und mir macht es großen Spass mit Kindern/Jungendlichen zusammen zu arbeiten\n- Es ergibt sich einen win-win Situation, da ich von den Schülern lerne und Sie von mir (Kommunikation, Flexibilität im Umgang von neuen Medien, Empathiefähigkeit, etc.)\n- Da ich bereits bei Tatendrang aktiv gewesen bin, möchte ich gerne wieder etwas sinnvolles für unsere Gesellschaft tun', 'uploads/2017101215.MP4', '20171219123710', '0', '80339', NULL, NULL, NULL, NULL, NULL, NULL, 'Projektmanagerin', 'Hilfreicher Austausch zwischen dem Schüler und seinem Mentor. Ankerpunkt bei Fragen sein und mein Wissen weiter geben.', 'Durch eine Arbeitskollegin. ', 'Kazmairstrasse 39', 'Germany (Deutschland)', '', 'Allianz Leben HV', '80339 München', 'BWL', NULL, '12-10-2017');
INSERT INTO `basicinformationtemp` VALUES (74, NULL, 'Arthur', 'Recktenwald', 'M2017-12206841819', NULL, '14-12-1966', NULL, NULL, 'Politik, Geocaching, Photographie', 'Arthur Recktenwald', 'Mentor', 'arthur.recktenwald@gmx.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Promotion', NULL, 'Wandern, Radfahren', NULL, NULL, 'Soziales Engagement ist mir wichtig und das Programm klingt sehr interessant.', 'uploads/2017102017.mp4', '20171219123710', '0', '70178 Stuttgart', NULL, NULL, NULL, NULL, NULL, NULL, 'Referatsleiter', 'Es soll beiden Seiten etwas bringen und auch Spass machen', 'MyFinance', 'Reinsburgstrasse 19', 'Germany (Deutschland)', '', 'Allianz Deutschland AG', '70178 Stuttgart', 'Physik', NULL, '21-10-2017');
INSERT INTO `basicinformationtemp` VALUES (75, NULL, 'Klaus', 'Dr. Samwer', 'M2017-12727789119', NULL, '18-10-1948', NULL, NULL, 'Politik, Finanzen, Sport, Pop, Ehrenamtliches Engagement', 'Klaus Dr. Samwer', 'Mentor', 'Klaus.Samwer@Googlemail.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Promotion', NULL, 'Radfahren, Skilaufen, Tennis', NULL, NULL, 'Ich engagiere mich gerne für die Jugend', 'uploads/2017110844.jpg', '20171219123710', '0', '50999 Köln', NULL, NULL, NULL, NULL, NULL, NULL, 'Pensionär', 'Gute Gespräche', 'Mein Engagement bei My Finance Coach', 'Schubertstr. 6', 'Germany (Deutschland)', '', 'Allianz Deutschland AG', '50672 Köln', 'Jura', NULL, '08-11-2017');
INSERT INTO `basicinformationtemp` VALUES (76, NULL, 'Priska ', 'Sutor', 'M2017-12154829519', NULL, '16-02-1983', NULL, NULL, 'Wirtschaft, Reisen, Tennis, Golf, Kitesurfen,', 'Priska  Sutor', 'Mentor', 'priska.sutor@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Fachbezogenen Realschule', 'Berufsausbildung', NULL, 'Golf u. Tennis', NULL, NULL, 'Weil ich gerne anderen Mensch was mit auf Ihren Lebensweg geben möchte.', 'uploads/2017110846.wmv', '20171219123710', '0', '7j', NULL, NULL, NULL, NULL, NULL, NULL, 'Referentin Bilanzierung', 'Das ich selbst meine Persönlichkeit verbessern kann und dass wir gemeinsam Zusammenarbeiten', 'über mein Finance Coach ', 'Isarweg', 'Germany (Deutschland)', '', 'Allianz Deutschland AG', '85774, Unterföhring ', 'Steuerfachangestellte / gepf. Bilanzbuchhalter', NULL, '08-11-2017');
INSERT INTO `basicinformationtemp` VALUES (77, NULL, 'Diana', 'Seibold', 'M2017-12174444619', NULL, '12091969', NULL, NULL, 'Pferde, Reisen', 'Diana Seibold', 'Mentor', 'diana.seibold@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'ja', 'Promotion', NULL, 'Reiten', NULL, NULL, 'Ich möchte gerne einem jungen Menschen bei seiner Berufs- oder Ausbildungswahl  helfen. Ich bin selbst Coach und habe auch die Tools und Erfahrung hier qualifiziert zu unterstützen. Da ich selbst im Recruting und HR Marketing/HR gearbeitet habe bzw. arbeite, kenne ich die Anforderungen. Außerdem habe ich über den zweiten Bildungsweg studiert und kann auch hier wertvolle Ratschläge geben. ', 'uploads/2017112087.MOV', '20171219123710', '0', '80802 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Head of Global Operations & IT Academy', 'Engagement und Offenheit', 'Allianz Intranet', 'Königinstr. 28', 'Germany (Deutschland)', '', 'Allianz SE', '80802 München', 'BWL und Berufsausbildung als Verwaltungsfachangestellte', NULL, '20-11-2017');
INSERT INTO `basicinformationtemp` VALUES (78, NULL, 'Konstantin', 'Riedel', 'M2017-12228366419', NULL, '26.04.1989', NULL, NULL, 'Technik, Computerspiele, Lesen, Reisen', 'Konstantin Riedel', 'Mentor', 'konstantin.riedel@allianz.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Master', NULL, 'Badminton, Schwimmen, Zumba, Tanzen', NULL, NULL, 'Das neue online Format klingt super interessant deshalb möchte ich es gerne ausprobieren. Mehr im Video ;)', '', '20171219123710', '0', '80336 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Digital Asset Optimization Expert', 'Einen offenen Mentee zu bekommen der viel Interesse zeigt und dem ich bei der Berufs-/Studienwahl weiterhelfen kann; Persönliche erfahrungen sammeln im Bereich Mentoring; Interessanter Austausch mit dem Mentee und von ihm/ihr etwas lernen.', 'Christine', 'Landwehrstr. 41 ', 'Germany (Deutschland)', '', 'Allianz SE', 'Artelierstr. 16, 81671 München', 'Wirtschaftsingenieur', NULL, '28-11-2017');
INSERT INTO `basicinformationtemp` VALUES (79, NULL, 'Eva', 'Niehaus', 'M2017-125406083719', NULL, '27-01-1986', NULL, NULL, 'Kochen, Reisen in ferne Länder', 'Eva Niehaus', 'Mentor', 'eva.niehaus@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Skifahren, Tennis spielen, Pilates', NULL, NULL, 'Mir stand stets ein Mentor mit Rat und Tat zur Seite und dies war mir immer eine große Hilfe bei schulischen/beruflichen Entscheidungen. Ich freue mich daher auf eine spannende Zeit mit meinem Mentee und hoffe, ihm/ihr eine ebenso große Unterstützung bieten zu können.', 'uploads/2017112994.mp4', '20171219123710', '0', '80337 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Senior Consultant', 'Erfahrungsaustausch mit dem Mentee aber auch unter den Mentoren und mit MFC Kollegen', 'Durch Christine ', 'Waltherstrasse 25', 'Germany (Deutschland)', '', 'Allianz Technoloogy SE', 'München', 'BWL', NULL, '29-11-2017');
INSERT INTO `basicinformationtemp` VALUES (80, NULL, 'Martin', 'Tietz', 'M2017-12170136619', NULL, '30-04-1966', NULL, NULL, 'Meine Töchter, Bier brauen', 'Martin Tietz', 'Mentor', 'martin.tietz@me.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Berufsausbildung', NULL, 'Zirkeltraining bei Wind & Wetter', NULL, NULL, 'Erfahrung mit Jugendlichen sammeln und lernen welche Wünsche vorhanden sind und welche Hürden zur Erfüllung genommen werden müssen', 'uploads/2017120173.MOV', '20171219123710', '0', '81377 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Versicherungsbetriebswirt', 'Viele Kontakte mit dem Mentee und natürlich SPASS. Vielleicht sogar ein bleibender Kontakt über das Programm hinaus !', 'Als Trainer von MFC', 'Reulandstrasse 30', 'Germany (Deutschland)', '', 'Allianz Global Corporate & Specialty', '81737 München', 'Versicherungsbetriebswirt DVA, FCII, Associate Risk Manager', NULL, '01-12-2017');
INSERT INTO `basicinformationtemp` VALUES (81, NULL, 'Anja', 'Schauberer', 'M2017-12320078719', NULL, '07-05-1974', NULL, NULL, 'Sport und Fremdsprachen', 'Anja Schauberer', 'Mentor', 'anja.schauberer@gmx.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Radeln, Schwimmen, Skifahren, Wandern', NULL, NULL, 'Ich würde gerne einem jungen Menschen ein paar Ideen, Inspirationen und Impulse für die Berufswahl geben. ', 'uploads/2017120588.mp4', '20171219123710', '0', '81673', NULL, NULL, NULL, NULL, NULL, NULL, 'Prozessberater Finanz- und Rechnungswesen', 'Etwas \'Guidance\' durch das Programm, z.B. durch die Online-Fragebögen.', 'My Finance Coach Programm - bin im Verteiler', 'Rahel-Straus-Weg 22', 'Germany (Deutschland)', '', 'Giesecke & Devrient GmbH', '81677  München', 'Betriebswirtschaft', NULL, '05-12-2017');
INSERT INTO `basicinformationtemp` VALUES (82, NULL, 'Viktoria', 'Leonhard ', 'M2017-12266555419', NULL, '20-11-1984', NULL, NULL, 'Krimis lesen, Sport (Fitness) und Reisen ', 'Viktoria Leonhard ', 'Mentor', 'viktoria.leonhard@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur ', 'Promotion', NULL, 'Fitness ', NULL, NULL, '- Interesse an Arbeit mit Jugendlichen \n- Leidenschaft für Persönlichkeitsentwicklung\n- Berufswahl/-orientierung begleitet mich auch beruflich. Ich kenne mich mit dem Thema gut aus. \n- My Finance Coach ist ebefalls ein tolles Format. Ich freue mich darauf, etwas Neues auszuprobieren. ', 'uploads/2017121797.MOV', '20171219123710', '0', '63322', NULL, NULL, NULL, NULL, NULL, NULL, 'Personaler', '- Engagierte Schüler/innen, die motiviert mitmachen wollen und viele Fragen stellen ', '- Intranet ', 'Kinzigstraße 4', 'Germany (Deutschland)', 'Hungary (Magyarország)', 'Allianz ', 'Frankfurt 60596 ', 'BWL und Chemie und Psychologie', NULL, '17-12-2017');
INSERT INTO `basicinformationtemp` VALUES (83, NULL, 'Laura', 'Kluge', 'M2017-121940686519', NULL, '15-02-1973', NULL, NULL, 'Reisen, Lesen, Familie, Ehrenamtliche Tätigkeit, Musik', 'Laura Kluge', 'Mentor', 'laura.kluge@live.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Nordic Walking, Radfahren', NULL, NULL, 'Ich möchte gerne interessierte Jugendliche bei verschiedenen Fragestellungen zu Schule, Beruf und Ausbildung unterstützen und gerne mein Wissen zu diesen Themen weitergeben. Ich finde dieses Mentoring/Coaching-Programm sehr sinnvoll, um eine gute Basis für den Berufseinstieg zu schaffen.', 'uploads/201712182.mp4', '20171219123710', '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, 'Market Data Manager', 'Fachliche und technische Unterstützung , qualitatives Feedback, persönliche Weiterentwicklung', 'My Financecoach', 'Lönsstrasse', 'Germany (Deutschland)', '', 'Allianz Global Investors GmbH', '60323 Frankfurt', 'BWL / Steuerfachangestellte', NULL, '18-12-2017');
