INSERT INTO `basicinformationtemp` VALUES (89, NULL, 'Rebecca', 'Wittbold', NULL, '2018-011744202', '13-10-2003', NULL, NULL, 'Tanzen, Singen, Schauspielern, Geige spielen, Umweltaktivitäten', 'Rebecca Wittbold', 'Mentee', 'rebeccawittbold@gmail.com', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '8. Klasse', '10. Klasse', 'Yes', 'Tanzen', 'Heinrich-Böll-Schule', 'Rodgau', 'Ich möchte mitmachen, weil ich mich dafür interessiere. Ich habe auch bei My Finance Coach mitgemacht und das hat mir sehr viel Spaß gemacht.', 'uploads/2017121213.mp4', '20180102095910', '0', '63110 Rodgau', NULL, 'Gesamtschule', '', NULL, NULL, '', NULL, NULL, NULL, 'Niederwiesenring 3', 'Germany (Deutschland)', '', NULL, NULL, NULL, NULL, '12-12-2017');
INSERT INTO `basicinformationtemp` VALUES (90, NULL, 'Leonie', 'Cormann', NULL, '2018-011881502', '16-12-2017', NULL, NULL, 'Klavier, Singen und Freunde treffen', 'Leonie Cormann', 'Mentee', 'leonie.cormann@gmx.de', NULL, NULL, NULL, NULL, NULL, NULL, NULL, '9. Klasse', '12. Klasse', 'Yes', 'Tennis, Kunstturnen und Skifahren', 'Gymnasium Oberhaching', 'Oberhaching', 'Sehr gerne möchte ich bei dem My Mentor Programm mitmachen. Vor zwei Jahren habe ich mit meinem Team beim Bundeswettbewerb Finanzen gewonnen und durfte letztes Jahr und darf dieses Jahr wieder in der Jury teilnehmen, was mir unglaublich Spaß macht. \nIch fand es sehr spannend einen Einblick in die Wirtschaftswelt zu bekommen und würde mich sehr freuen, noch mehr Informationen und Eindrücke in diesem Bereich sammeln zu dürfen.\nDa ich derzeit noch nicht weiß, was ich in der Zukunft machen will, bin ich sehr gespannt auf weitere Ideen und den Austausch mit einer erfahrenen Person. Gerne würde ich einige Zeit im Ausland verbringen und danach vielleicht studieren. Aber momentan bin ich noch nicht festgelegt. \nGerne möchte ich mich auch persönlich weiterentwickeln, wobei mir mein Mentor (oder Mentorin) helfen könnte. Außerdem bin ich sehr offen für neue Erfahrungen und lerne gerne neue Leute kennen. Deshalb würde ich mich sehr freuen am My Mentor Programm teilzunehmen.\n', 'uploads/2017121776.MOV', '20180102095910', '0', '82041 Oberhaching', NULL, 'Gymnasium', '', NULL, NULL, '', NULL, NULL, NULL, 'Fritz- Kömig- Weg 8', 'Germany (Deutschland)', '', NULL, NULL, NULL, NULL, '17-12-2017');
