INSERT INTO `basicinformationtemp` VALUES (108, NULL, 'Priska ', 'Sutor', 'M2018-01435379402', NULL, '16-02-1983', NULL, NULL, 'Wirtschaft, Reisen, Tennis, Golf, Kitesurfen,', 'Priska  Sutor', 'Mentor', 'priska.sutor@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Fachbezogenen Realschule', 'Berufsausbildung', NULL, 'Golf u. Tennis', NULL, NULL, 'Weil ich gerne anderen Mensch was mit auf Ihren Lebensweg geben möchte.', 'uploads/2017110846.wmv', '20180102102001', '0', '7j', NULL, NULL, NULL, NULL, NULL, NULL, 'Referentin Bilanzierung', 'Das ich selbst meine Persönlichkeit verbessern kann und dass wir gemeinsam Zusammenarbeiten', 'über mein Finance Coach ', 'Isarweg', 'Germany (Deutschland)', '', 'Allianz Deutschland AG', '85774, Unterföhring ', 'Steuerfachangestellte / gepf. Bilanzbuchhalter', NULL, '08-11-2017');
INSERT INTO `basicinformationtemp` VALUES (109, NULL, 'Diana', 'Seibold', 'M2018-01543038302', NULL, '12091969', NULL, NULL, 'Pferde, Reisen', 'Diana Seibold', 'Mentor', 'diana.seibold@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'ja', 'Promotion', NULL, 'Reiten', NULL, NULL, 'Ich möchte gerne einem jungen Menschen bei seiner Berufs- oder Ausbildungswahl  helfen. Ich bin selbst Coach und habe auch die Tools und Erfahrung hier qualifiziert zu unterstützen. Da ich selbst im Recruting und HR Marketing/HR gearbeitet habe bzw. arbeite, kenne ich die Anforderungen. Außerdem habe ich über den zweiten Bildungsweg studiert und kann auch hier wertvolle Ratschläge geben. ', 'uploads/2017112087.MOV', '20180102102001', '0', '80802 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Head of Global Operations & IT Academy', 'Engagement und Offenheit', 'Allianz Intranet', 'Königinstr. 28', 'Germany (Deutschland)', '', 'Allianz SE', '80802 München', 'BWL und Berufsausbildung als Verwaltungsfachangestellte', NULL, '20-11-2017');
INSERT INTO `basicinformationtemp` VALUES (110, NULL, 'Konstantin', 'Riedel', 'M2018-01159530302', NULL, '26.04.1989', NULL, NULL, 'Technik, Computerspiele, Lesen, Reisen', 'Konstantin Riedel', 'Mentor', 'konstantin.riedel@allianz.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Master', NULL, 'Badminton, Schwimmen, Zumba, Tanzen', NULL, NULL, 'Das neue online Format klingt super interessant deshalb möchte ich es gerne ausprobieren. Mehr im Video ;)', '', '20180102102001', '0', '80336 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Digital Asset Optimization Expert', 'Einen offenen Mentee zu bekommen der viel Interesse zeigt und dem ich bei der Berufs-/Studienwahl weiterhelfen kann; Persönliche erfahrungen sammeln im Bereich Mentoring; Interessanter Austausch mit dem Mentee und von ihm/ihr etwas lernen.', 'Christine', 'Landwehrstr. 41 ', 'Germany (Deutschland)', '', 'Allianz SE', 'Artelierstr. 16, 81671 München', 'Wirtschaftsingenieur', NULL, '28-11-2017');
INSERT INTO `basicinformationtemp` VALUES (111, NULL, 'Eva', 'Niehaus', 'M2018-01846375702', NULL, '27-01-1986', NULL, NULL, 'Kochen, Reisen in ferne Länder', 'Eva Niehaus', 'Mentor', 'eva.niehaus@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Skifahren, Tennis spielen, Pilates', NULL, NULL, 'Mir stand stets ein Mentor mit Rat und Tat zur Seite und dies war mir immer eine große Hilfe bei schulischen/beruflichen Entscheidungen. Ich freue mich daher auf eine spannende Zeit mit meinem Mentee und hoffe, ihm/ihr eine ebenso große Unterstützung bieten zu können.', 'uploads/2017112994.mp4', '20180102102001', '0', '80337 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Senior Consultant', 'Erfahrungsaustausch mit dem Mentee aber auch unter den Mentoren und mit MFC Kollegen', 'Durch Christine ', 'Waltherstrasse 25', 'Germany (Deutschland)', '', 'Allianz Technoloogy SE', 'München', 'BWL', NULL, '29-11-2017');
INSERT INTO `basicinformationtemp` VALUES (112, NULL, 'Martin', 'Tietz', 'M2018-01539176402', NULL, '30-04-1966', NULL, NULL, 'Meine Töchter, Bier brauen', 'Martin Tietz', 'Mentor', 'martin.tietz@me.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Berufsausbildung', NULL, 'Zirkeltraining bei Wind & Wetter', NULL, NULL, 'Erfahrung mit Jugendlichen sammeln und lernen welche Wünsche vorhanden sind und welche Hürden zur Erfüllung genommen werden müssen', 'uploads/2017120173.MOV', '20180102102001', '0', '81377 München', NULL, NULL, NULL, NULL, NULL, NULL, 'Versicherungsbetriebswirt', 'Viele Kontakte mit dem Mentee und natürlich SPASS. Vielleicht sogar ein bleibender Kontakt über das Programm hinaus !', 'Als Trainer von MFC', 'Reulandstrasse 30', 'Germany (Deutschland)', '', 'Allianz Global Corporate & Specialty', '81737 München', 'Versicherungsbetriebswirt DVA, FCII, Associate Risk Manager', NULL, '01-12-2017');
INSERT INTO `basicinformationtemp` VALUES (113, NULL, 'Anja', 'Schauberer', 'M2018-01266308102', NULL, '07-05-1974', NULL, NULL, 'Sport und Fremdsprachen', 'Anja Schauberer', 'Mentor', 'anja.schauberer@gmx.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Radeln, Schwimmen, Skifahren, Wandern', NULL, NULL, 'Ich würde gerne einem jungen Menschen ein paar Ideen, Inspirationen und Impulse für die Berufswahl geben. ', 'uploads/2017120588.mp4', '20180102102001', '0', '81673', NULL, NULL, NULL, NULL, NULL, NULL, 'Prozessberater Finanz- und Rechnungswesen', 'Etwas \'Guidance\' durch das Programm, z.B. durch die Online-Fragebögen.', 'My Finance Coach Programm - bin im Verteiler', 'Rahel-Straus-Weg 22', 'Germany (Deutschland)', '', 'Giesecke & Devrient GmbH', '81677  München', 'Betriebswirtschaft', NULL, '05-12-2017');
INSERT INTO `basicinformationtemp` VALUES (114, NULL, 'Viktoria', 'Leonhard ', 'M2018-01218670602', NULL, '20-11-1984', NULL, NULL, 'Krimis lesen, Sport (Fitness) und Reisen ', 'Viktoria Leonhard ', 'Mentor', 'viktoria.leonhard@allianz.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur ', 'Promotion', NULL, 'Fitness ', NULL, NULL, '- Interesse an Arbeit mit Jugendlichen \n- Leidenschaft für Persönlichkeitsentwicklung\n- Berufswahl/-orientierung begleitet mich auch beruflich. Ich kenne mich mit dem Thema gut aus. \n- My Finance Coach ist ebefalls ein tolles Format. Ich freue mich darauf, etwas Neues auszuprobieren. ', 'uploads/2017121797.MOV', '20180102102001', '0', '63322', NULL, NULL, NULL, NULL, NULL, NULL, 'Personaler', '- Engagierte Schüler/innen, die motiviert mitmachen wollen und viele Fragen stellen ', '- Intranet ', 'Kinzigstraße 4', 'Germany (Deutschland)', 'Hungary (Magyarország)', 'Allianz ', 'Frankfurt 60596 ', 'BWL und Chemie und Psychologie', NULL, '17-12-2017');
INSERT INTO `basicinformationtemp` VALUES (115, NULL, 'Laura', 'Kluge', 'M2018-01345150002', NULL, '15-02-1973', NULL, NULL, 'Reisen, Lesen, Familie, Ehrenamtliche Tätigkeit, Musik', 'Laura Kluge', 'Mentor', 'laura.kluge@live.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Diplom', NULL, 'Nordic Walking, Radfahren', NULL, NULL, 'Ich möchte gerne interessierte Jugendliche bei verschiedenen Fragestellungen zu Schule, Beruf und Ausbildung unterstützen und gerne mein Wissen zu diesen Themen weitergeben. Ich finde dieses Mentoring/Coaching-Programm sehr sinnvoll, um eine gute Basis für den Berufseinstieg zu schaffen.', 'uploads/201712182.mp4', '20180102102001', '0', '2', NULL, NULL, NULL, NULL, NULL, NULL, 'Market Data Manager', 'Fachliche und technische Unterstützung , qualitatives Feedback, persönliche Weiterentwicklung', 'My Financecoach', 'Lönsstrasse', 'Germany (Deutschland)', '', 'Allianz Global Investors GmbH', '60323 Frankfurt', 'BWL / Steuerfachangestellte', NULL, '18-12-2017');
INSERT INTO `basicinformationtemp` VALUES (116, NULL, 'Simon', 'Leugner', 'M2018-01152784302', NULL, '03-11-1990', NULL, NULL, 'Reisen, Brettspiele, Finanzanlagen, Kochen und Essen', 'Simon Leugner', 'Mentor', 'simon.leugner@googlemail.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Bachelor', NULL, 'Tischtennis, Golf, Joggen', NULL, NULL, 'Mentoring bedeutet für mich einen jungen Menschen zu begleiten und zu fördern. Ihn dazu zu bringen an sich und seine Ziele zu glaub, und zu lernen diese Ziele richtig zu entwickeln und zu erreichen. \nIch hatte immer das Glück Mensch um mich zu haben, die ein Interesse daran hatten mich zu fördern und ich habe auch schon seit langem Spaß daran Menschen zu fördern. \nMeine ersten Schritte waren mit 16 als Tischtennistrainer. Mit 19 bin ich als Au Pair nach Amerika um dort auf 4 Kinder aufzupassen und diese zu unterstützen. Mein dortiger Gastvater war selfmade Millionär von dem ich viel zum Thema Zielsetzung und Zielerreichung mitnehmen konnte. Seit dem beschäftige ich mich mit Trainern wie Bodo Schäfer, Anthony Robbins etc. \nZurück in Deutschland hat mich mein erster richtiger Coach gefunden. Er hat mir geholfen mich, nach meiner Ausbildung zum Kaufmann für Finanzen, als Finanzberater selbstständig zu machen. Nebenbei habe ich ein Wirtschaftswissenschaftliches Studium absolviert. Nach 4 Jahren Selbstständigkeit bin ich nun in die Unternehmensberatung gewechselt um mich weiterzuentwickeln. \nDiese Erfahrungen, Unterstützung und das Gedankengut was mir zu Teil wurde möchte ich gerne weitergeben. \nÜber eine Einladung als Mentor würde ich mich sehr freuen. \nSimon Leugner', 'uploads/2017122058.MOV', '20180102102001', '0', '60385 Frankfurt', NULL, NULL, NULL, NULL, NULL, NULL, 'Consultant', 'Ich hoffe jemanden dabei helfen zu können sich zu entwickeln und selbst daran zu wachsen', 'Firma sucht nach Menschen die darauf Lust haben. Ich hatte mich schon vorab für das my finace coach Programm interessiert. ', 'Saalburgallee 18', 'Germany (Deutschland)', '', 'Findic GmbH / zeb Company', '60325 Frankfurt', 'Wirtschaftswissenschaften / Ausbildung als Kaufmann für Versicherungen und Finanzen', NULL, '20-12-2017');
INSERT INTO `basicinformationtemp` VALUES (117, NULL, 'Alexey', 'Kaminskiy', 'M2018-0113771787802', NULL, '01-06-1986', NULL, NULL, 'IFRS 9 & Accounting, Ökonomische Sachverhalte, Weltgeschehen & Politik, Fußball & Sport, Länder & Kulturen, neues Wissen', 'Alexey Kaminskiy', 'Mentor', 'akaminskiy@zeb.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Master', NULL, 'Fussball, Joggen, Schwimmen, gelegentliches Work-out, früher Sporttanzen, generell Aktivitäten draußen wie Radfahren oder Wandern', NULL, NULL, 'Aus meiner Sicht bekommen die Schüler heutzutage nur wenig praktische Einblicke in die auf sie künftig zukommende Arbeits- und auch Wirtschaftswelt. Deshalb fällt es vielen oft schwer, sich zu orientieren, für welchen Beruf man sich entscheiden soll, welche Ausbildung hierzu optimal ist und welche Herausforderungen einen in der Arbeitswelt künftig erwarten.\nAus eigener Erfahrung möchte ich den Mentee bei seiner Berufsfindung unterstützen, relevante Einblicke in die Arbeitswelt geben sowie potentielle Herausforderungen beleuchten. Wichtig ist dabei, die Interessen des Mentees zu verstehen und auf der Basis relevante Tipps für erfolgreiche und harmonische Ausbildung, Berufseinstieg und Karriereweg zu geben.', 'uploads/2017122010.mp4', '20180102102001', '0', '60325 Frankfurt am Main', NULL, NULL, NULL, NULL, NULL, NULL, 'Management Consultant', 'Interesse und Engagement des Mentees, proaktives Sammeln und Stellen von Fragen (Vorbereitung auf die Austauschtermine), Upward-Feedback seitens des Mentees für eigene Weiterentwicklung im Mentoring', 'Corporate Volunteering Programm beim zeb', 'Taunusanlage 19', 'Germany (Deutschland)', '', 'zeb', '48153 Münster', 'M.A. Management. Vertiefungsrichtung Controlling & Finanzen', NULL, '20-12-2017');
INSERT INTO `basicinformationtemp` VALUES (118, NULL, 'Michael', 'Kühnelt', 'M2018-01544999502', NULL, '12041978', NULL, NULL, 'Meine Familie ;-)', 'Michael Kühnelt', 'Mentor', 'michael.kuehnelt@zeb.de', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Realschulabschluss', 'Bachelor', NULL, 'Laufen, Schwimmen, Radfahren', NULL, NULL, 'Weil ich selbst nur über Umwege zu meinem Traumberuf gekommen bin und meine dabei gewonnenen Erfahrungen gern weitergeben möchte.', 'uploads/2017122114.MOV', '20180102102001', '0', '31226 Peine', NULL, NULL, NULL, NULL, NULL, NULL, 'Unternehmensberater', 'Einen offenen und freundschaftlichen Austausch mit meinem Mentee.', 'Durch meinen Arbeitgeber.', 'Maikäferring 8', 'Germany (Deutschland)', '', 'zeb rsa GmbH', '48153 Münster', 'Bankbetriebslehre / Bankkaufmann', NULL, '21-12-2017');
INSERT INTO `basicinformationtemp` VALUES (119, NULL, 'Plamen', 'Todorov', 'M2018-013986625002', NULL, '15-06-1989', NULL, NULL, 'Autos', 'Plamen Todorov', 'Mentor', 'plamen.todorov.de@gmail.com', '', NULL, NULL, NULL, NULL, NULL, NULL, 'Abitur', 'Bachelor', NULL, 'Fußball', NULL, NULL, 'Die Kommunikation mit jungen Leuten macht mir Spaß - dies hat sich auch im Rahmen meiner Tätigkeit als Finance Coach bestätigt. Hinsichlich meinem internationalen Hintergrund (bin in Bulgarien aufgewachsen und erst mit 19 nach Deutschland umgezogen) glaube ich, dass ich meine Erfahrungen einsetzen kann und jüngen Menschen (auch mit Migrationshintergrund) helfen, das Beste aus ihrer Schulausbildung zu machen.', 'uploads/2017122267.mp4', '20180102102001', '0', '80802', NULL, NULL, NULL, NULL, NULL, NULL, 'Product Startegy Associate', 'Ich freue mich auf einen Mentee, der/die ehrgeizig und offen für Ratschläge ist', 'Carmen Regelin', 'Haimhauserstr. 10, München', 'Bulgaria (България)', '', 'PIMCO Deutschland GmbH', '80335 München', 'Betriebswirtschaftslehre', NULL, '22-12-2017');
