INSERT INTO `content` VALUES (125, 'E2 Ausbildung oder Schule? ALT', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                <p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\">Überlegst du zu <font style=\"\"><span style=\"font-weight: bold;\">studieren</span></font>? Bei diesem Test kannst du erfahren was zu dir passt und in welche Richtung es gehen kann:</span><font style=\"\"> &nbsp;<a href=\"http://www.was-studiere-ich.de/\" target=\"_blank\">http://www.was-studiere-ich.de/</a></font></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\">oder eher eine <font style=\"\"><span style=\"font-weight: bold;\">Ausbildung</span></font> zu machen? Finde heraus welche Berufe zu dir passen:</span> <font style=\"\"><a href=\"https://set.arbeitsagentur.de/teaser\" target=\"_blank\" style=\"\">https://set.arbeitsagentur.de/teaser</a></font></span><br>                        </p>                                                                                                                                                ', NULL, '20171010124728138', 'Berufsorientierung', '', '', 185, 'Lösung hochladen', 1, '						Das ist die Mentorbeschreibung E2                        ', 'In Deutschland gibt es alleine mehr als 5000 Studien- und Ausbildungsmöglichkeiten', 1, NULL);
INSERT INTO `content` VALUES (126, '(I3) Projektplanung II', 'Informieren', 'Video', '                                                                                                                                                                                                                                                                                                <div style=\"text-align: justify;\" align=\"center\"><font style=\"text-align: left; font-size: 14px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\">Bestimmt hast du selbst schon einmal ein Projekt&nbsp;organisiert oder bei der Umsetzung einer Idee mitgemacht. Vielleicht hast du dabei schon festgestellt, dass das manchmal gar nicht so leicht ist. Ein Projekt zu organisieren und durchzuführen erfordert eine gute Planung und Aufgabenverteilung. In folgendem Video erfährst du was es alles zu beachten gibt!</span></span>&nbsp;&nbsp;</font><span style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></div>                                                                                                                                                                                                                                                ', NULL, '20170718094815659', 'Arbeitsmethoden', '<iframe width=\"854\" height=\"480\" src=\"https://www.youtube.com/embed/C0O4WJudmo0\" frameborder=\"0\" allowfullscreen></iframe>', 'Link', 149, '', 0, '', 'Projekte planen - Und wie geht man dabei am besten vor?', 1, NULL);
INSERT INTO `content` VALUES (129, '(I1) - Termine', 'Informieren', 'Lesestoff', '<br><div style=\"text-align: center;\"><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Digitale Programme...was ist denn damit gemeint? </span></span></font></span></span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Und was gibt es da für welche? </span></span></font></span></span></span>Und vor allem...warum fangen wir damit an? Fragen über Fragen..<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Die Arbeit am PC ist im Laufe der Zeit immer wichtiger geworden und zählt deshalb mit zu den bedeutendsten Arbeitsmethoden!</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"> Ob in der Schule, in der Ausbildung, im Studium oder im Arbeitsleben, überall sind gewisse Kenntnisse im Umgang mit verschiedenen digitalen Programmen notwendig und nicht mehr wegzudenken. Außerdem sind PC-Kenntnisse nicht nur wichtig wenn man später mal im Büro arbeitet sondern zum Beispiel auch in vielen anderen Bereichen wie der Landwirtschaft, der Medizin oder in Handwerksbetrieben.<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Und weil du ja der eigentliche \"digital native\" und Experte auf diesem Gebiet bist, weißt du bestimmt sowieso schon super viel über die Welt der Computer! Manche Programme aber sind ganz besonders wichtig und man braucht sie immer wieder: Zum Beispiel Microsoft Office Word zum Erstellen von Texten, Power Point zur Gestaltung von Präsentationen und Excel zur Arbeit mit Zahlen.</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">In diesem Teil geht es nun erstmal um das Programm Word und das Erstellen einer PDF-Datei. So kannst du erst einen Text schreiben und den dann danach in eine Datei umwandeln, die keiner mehr so leicht verändern kann! Das ist manchmal total praktisch und du kannst die Funktion auch gut brauchen wenn eine Aufgabe hier bei My Mentor darin besteht, eine von dir erstellte Datei hochzuladen.<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Wie es geht kannst du ganz einfach in dieser Kurzanleitung nachlesen! Wenn du dich schon damit auskennst, kannst du den Teil auch überspringen und die Anleitung zum Nachlesen im Kopf behalten!</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span></font></span></span></span></p></div><img style=\"width: 432.417px; height: 432.417px;\" src=\"../../upload/files/79balloon-1345879_1920.png		\"><div style=\"text-align: center;\"><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span></font></span></span></span></p></div>                                                                                                                                                                                                                                                                                                                                                ', NULL, '20171009144610332', 'Arbeitsmethoden', '', '', 329, 'Lösung hochladen', 0, '', 'Hey! Schön, dass du hier bist! Und los geht\'s mit dem Baustein Arbeitsmethoden! Lass uns gleich mit dem ersten spannenden Thema starten: Digitale Programme', 1, NULL);
INSERT INTO `content` VALUES (130, 'Konfliktlösung: dein Verhalten', 'Informieren', 'Aufgabe', '                                                                                                                                                                                                                        <p style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Thema Konfliktlösung: Konflikten ist jeder ausgesetzt, dann wenn es gerade mit den Eltern stressig oder weil die Freunde nicht wie erwartet dich unterstützt haben. Doch diese zu umgehen ist nicht immer die beste Alternative.</span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\"> In diesem </span><span style=\"font-weight: bold;\"><font style=\"\"><a href=\"http://www.jugend-und-bildung.de/webcom/show_article.php/_c-946/_nr-9/i.html\" target=\"_blank\">Test</a></font></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\"><font style=\"\"> </font></span>kannst du rausfinden wie du in bestimmten Situationen reagieren würdest. Futter zum Nachdenken…und durchatmen!</span></span></p><p style=\"line-height: 1.2;\"></p><div style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><span style=\"color: rgb(0, 0, 0);\"><br></span><p></p><p><span style=\"color: rgb(0, 0, 0);\"><br></span><span style=\"color: rgb(0, 0, 0);\"><br></span>                        </p>                                                                                                                                                                                                                        ', NULL, '20170718095226794', 'Arbeitsmethoden', '', '', 268, '', 0, '', 'Stärken aufzählen kann jeder - Warum ist eine Begründung essentiell?', 1, NULL);
INSERT INTO `content` VALUES (132, 'Informieren 3c - Der Lerntypentest', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">Nachdem du nun schon einiges über Tricks und Kniffe für erfolgreiches Lernen gehört und gelesen hast, wollen wir nun deinen individuellen Lerntyp bestimmen. Der zeigt dir nämlich, wie du dir Lerninhalte und Wissen am besten einprägen kannst. Zum Beispiel, wenn du dich fragst, wie du dir die neuen Vokabeln aus dem Englischunterricht am besten merken kannst. <br></p><p style=\"text-align: justify;\">Vielleicht klappt es am besten, wenn du sie dir häufig aufschreibst oder sie dir einige Male anhörst oder du dir kleine Zettel an bestimmte Gegenstände klebst. Jeder hat seine eigenen Methoden, wie er letztlich am besten mit neuem Lernstoff umgeht.<br></p><p style=\"text-align: justify;\">Neben Mischtypen, kann man zwischen vier groben Lerntypen unterscheiden. Und zwar den auditiven, den visuellen, den motorischen und den kommunikativen Lerntyp. Mithilfe des Tests kannst du herausfinden, welcher Lerntyp am besten auf dich zutrifft. Gleichzeitig findest du Strategien und Tricks, wie du versuchen kannst, die nächsten Vorbereitungen auf Prüfungen zu verbessern. Außerdem ist es hilfreich, immer wieder einfach ein bisschen auszuprobieren, welche Lernmethoden für dich geeignet sind.</p><div style=\"text-align: justify;\">Über die Download-Funktion geht’s direkt zum Test. Drucke ihn dir am besten aus und kreuze die zutreffenden Antworten mit einem Stift an (oder PDF-Bearbeitung). Die Auflösung gibt es ebenfalls zum Herunterladen. Viel Erfolg dabei!&nbsp;</div><p style=\"text-align: center;\"></p><div style=\"text-align: center;\"><img style=\"width: 538px;\" src=\"../../upload/files/42study-2746004_1920.jpg		\"></div><p><br></p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ', '2017-10-20', '20171020150019281', 'Arbeitsmethoden', '', '', 428, '', 0, '', 'Welcher Lerntyp bist du?', 0, '1513172263');
INSERT INTO `content` VALUES (133, 'Kommunikation, Rhetorik und Konfliktlösung', 'Informieren', 'Lesestoff', '                                                                                                                                                <p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Hier geht es darum, wie man es schafft, gute Referate zu halten: </span></p><p style=\"text-align: justify; line-height: 1.2;\"><a style=\"font-size: medium;\" href=\"http://karrierebibel.de/referat-halten/\" target=\"_blank\">http://karrierebibel.de/referat-halten/</a><br></p>                                                                                                                                                ', NULL, '20170718095505162', 'Arbeitsmethoden', '', '', 326, '', 0, '', '', 1, NULL);
INSERT INTO `content` VALUES (134, '(E1) Effizienter lernen', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: left; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px; text-align: justify;\">Hast du schon mal was von Lerntypen gehört? Ob Hören, Sehen oder das Sprechen mit anderen...jeder Mensch lernt gerne ein bisschen anders!</span></p><p style=\"text-align: justify; line-height: 1.4;\"><font style=\"font-size: 14px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\">Wenn du weißt welcher Lerntyp du bist, wirst du dir in jedem Fall beim Einprägen von neuen Dingen sehr viel leichter tun! </span></span><span style=\"color: rgb(0, 0, 0);\">Es kann aber auch sein, dass für dich\n eine Mischung aus Lerntypen zutrifft! Mache deshalb den Test und probiere es in Zukunft einfach einmal aus, indem du darauf\n achtest wie du am besten Lernen kannst.</span></font></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"font-size: 14px;\">Hier geht\'s zum Test: </font><br></span></span></p><div style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><span style=\"font-weight: bold; color: rgb(107, 165, 74);\"></span>                        <p></p>                                                                                                                                                                                                                                                                                                                                                ', NULL, '20170718095554705', 'Arbeitsmethoden', '', '', 188, 'Lösung hochladen', 0, '', 'Super, dass du jetzt schon viel zum Thema Arbeitsmethoden weißt..von Lernstrategien bis zum Umgang mit dem PC! Hier ist die erste richtige Aufgabe für dich...', 1, NULL);
INSERT INTO `content` VALUES (136, 'Erarbeiten 3 - Aufgabe zur Tabellenkalkulation', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">Zahlen, Tabellen, Rechnen - hier ist eine Aufgabe für dich!</p><p style=\"text-align: justify;\">Der Umgang mit Tabellenkalkulationsprogrammen ist am Anfang gar nicht so leicht und man braucht ein wenig Übung, bis man die wichtigsten Tricks und Kniffe beherrscht.</p><p style=\"text-align: justify;\">Dann ist dieses Programm allerdings sehr praktisch, denn man kann easy Übersichten erstellen, Ergebnisse berechnen, verschiedene Tabellen verknüpfen und vieles mehr. Versuche dich am besten gleich an dieser Übung. Lade dir dazu das Aufgabenblatt herunter und wende dich bei Fragen an deinen Mentor. Er/Sie wird dir gerne behilflich sein. Viel Erfolg!<br></p><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><img src=\"../../upload/files/2017102711.jpg		\" style=\"width: 538px;\"></div><div style=\"text-align: center;\"><br></div>                                                                                                                                                                                                                        <p></p>                                                                                                                                                                                                                                                                        ', NULL, '20170925125056377', 'Medienbildung', '', '', 183, 'Lösung hochladen', 0, '																								<p>Lieber Mentor,</p><p>der Umgang mit Excel ist in der heutigen Arbeitswelt unabdingbar. Auch wenn man kein Excel-Profi sein muss, erleichtern einem kleine Kniffe mit dem Programm den Arbeitsalltag. Bei dieser Aufgabe soll dein Mentee verschiedene Fahrkarten-Angebote für Paul berechnen. </p><p>Sollte dein Mentee Fragen haben und nicht weiterkommen, wendet er sich an dich. Besprich die Rechenwege mit deinem Mentee Schritt für Schritt und erkläre ihm die Klicks in Excel. Du kannst die Musterlösung hier herunterladen.<br></p><p>Komme bei Fragen gerne jederzeit auf uns zu.</p><p>Dein My Mentor-Team<br></p>                                                                                                ', 'Zahlen, Rechnen, Tabellen, jetzt bist du dran!', 0, '1512648359');
INSERT INTO `content` VALUES (137, 'E5a Bewerbungstraining ALT', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                                                                                                                                                                                        <div style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0); text-align: justify; font-size: 14px;\">Teste dich mit diesem kleinen Quiz dein Bewerbungswissen. Lade es anschließend hoch, so dass dein Mentor überprüfen kann ob auch alles passt.</span></div><div style=\"text-align: justify;\"><br></div><img src=\"../../upload/files/180beruf7.jpeg		\" style=\"width: 268.5px;\"><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span><span style=\"color: rgb(0, 0, 0);\"></span></div>                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ', NULL, '20170925095733682', 'Berufsorientierung', '', '', 212, 'Lösung hochladen', 0, '						<p><span style=\"color: rgb(0, 0, 0);\">Lieber Mentor,</span></p><p><span style=\"color: rgb(0, 0, 0);\">in diesem Quiz sollen die Mentees die Tipps und Tricks, die wir Ihnen für die Erstellung der Bewerbungsunterlagen gegeben haben, nochmals&nbsp; überprüfen.</span></p><p><span style=\"color: rgb(0, 0, 0);\">Die Auflösung zu den Multiple-Choice Fragen finden Sie&nbsp;</span><a href=\"https://myfinancecoach.box.com/s/od5ti1l7kz0y76d9tjinamznd4g5z99l\" target=\"_blank\">hier.</a><span style=\"color: rgb(0, 0, 0);\">&nbsp;Bitte überprüfen Sie die Antworten Ihres Mentees und korrigieren Sie diese</span><span style=\"color: rgb(0, 0, 0);\">&nbsp;ggfls.</span><span style=\"color: rgb(0, 0, 0);\"></span></p><div><span style=\"color: rgb(0, 0, 0);\"><br></span></div>						                                                ', 'Kommunikationsfähigkeiten sind unheimlich wichtig..ob es darum geht ein Referat zu halten oder Konflikte zu lösen. Und das kann man üben!', 1, NULL);
INSERT INTO `content` VALUES (139, '(I3) Projektplanung I', 'Informieren', 'Aufgabe', '                                                                                                                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: justify; line-height: 1.4;\" align=\"center\"><span style=\"color: rgb(0, 0, 0); background-color: inherit;\"><font size=\"3\">Hast du dich schon einmal um ein Projekt oder die Planung einer bestimmten Angelegenheit gekümmert? Beschreibe worum es dabei ging:</font></span></p><p style=\"text-align: justify; line-height: 1.4;\" align=\"left\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-size: 18px;\"><span style=\"font-size: 18px;\"><font size=\"3\"></font><font size=\"3\"></font><font size=\"3\"></font><br></span></span></span></span></p><strong>                       </strong>                                                                                                                                                                                                                                                                                                                                                                                                                         ', NULL, '20171005094708334', 'Arbeitsmethoden', '', '', 131, 'Lösung hochladen', 0, '', 'Wir kommen zu einem neuen Thema: Der Projektplanung! Immer wieder geht es darum, Projekte zu planen und Ideen umzusetzen. ', 1, NULL);
INSERT INTO `content` VALUES (140, 'Bewerbungstraining', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                <p style=\"line-height: 1.2;\"></p><div style=\"text-align: left; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); text-align: justify; font-size: 14px;\">Der häufig gehörte Rat „Sei einfach Du selbst“ sollte nicht auf die leichte Schulter genommen und mit allzu lässigem und unprofessionellem Auftreten verwechselt werden. Welche Stolperfallen es sonst noch zu umgehen gilt und wie das Gespräch zum Erfolg wird, erfährst Du hier:</span></div><p style=\"line-height: 1.4;\"></p><p style=\"text-align: justify; line-height: 1.4;\"><a href=\"https://schueler.deutschlands100.de/ausbildung/bewerbung/vorstellungsgespraech.html\" target=\"_blank\" style=\"font-size: medium;\">https://schueler.deutschlands100.de/ausbildung/bewerbung/vorstellungsgespraech.html</a></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><br></span></p><div style=\"text-align: center; line-height: 1.4;\"></div><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><br></span></p><div style=\"text-align: justify; line-height: 1.4;\"><br></div>                                                                                                                                                                                                                                                                                                ', NULL, '20170925125612567', 'Berufsorientierung', '', '', 142, '', 1, '', 'Was erwartet mich im Vorstellungsgespräch?', 1, NULL);
INSERT INTO `content` VALUES (141, 'Ein Plan hilft dir dabei!', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><span style=\"background-color: inherit; color: rgb(0, 0, 0);\">Hier findest du eine super Übersicht zum </span><a href=\"http://www.planet-beruf.de/fileadmin/assets/PDF/PDF_Checklisten/berufswahlfahrplan.pdf\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255);\">Berufswahlfahrplan&nbsp;</a>&nbsp;</span><span style=\"font-size: 14px; color: rgb(0, 0, 0);\">Wie sieht</span><span style=\"font-size: 14px; color: rgb(0, 0, 0); font-weight: bold;\"> dein Plan</span><span style=\"font-size: 14px; color: rgb(0, 0, 0);\"> ungefähr aus? Mach eine</span><span style=\"font-size: 14px; color: rgb(0, 0, 0); font-weight: bold;\"> Skizze</span><span style=\"font-size: 14px; color: rgb(0, 0, 0);\"> und lade diese anschließend hoch.</span></p><img src=\"../../upload/files/15beruf4.jpeg&#9;&#9;\" style=\"width: 537px;\"><p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></p>                                                                                                                                                                                                                                                                                                                                                                                                                        ', NULL, '20170720094113956', 'Berufsorientierung', '', '', 141, '', 0, '',