INSERT INTO `content` VALUES (143, 'abc', 'Erarbeiten', 'Lesestoff', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: center; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px; font-weight: bold;\">Die Sache mit dem Lernen ist manchmal gar nicht so einfach, doch es gibt&nbsp;einige Tipps und Tricks damit es dir leichter fällt… und Spaß machen kann es auch!</span></span></span></p><p style=\"text-align: center; line-height: 1.2;\"><font style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px; font-weight: bold;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span></span></span></font></p><p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\">The Simple Club zeigt dir, wie du deine Leistung beim Lernen ganz einfach prima steigern kannst! Schau gleich mal rein!</span></font><br></span></p>                                                                                                                                                                                                                                                                                                                         