INSERT INTO `content` VALUES (145, 'Informieren 3b - Ziele SMART formulieren!', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                <p style=\"text-align: justify;\">                        Erste Eindrücke davon, wie man Ziele SMART formuliert und umsetzt, konntest du im gleichnamigen Video (I3a) erhalten. </p><p style=\"text-align: justify;\">Damit du die dazugehörige Übung E3 gut und schnell meistern kannst und zukünftig all deine Ziele auf den Punkt bringst, erklären wir dir nun anhand eines Beispiels nochmal Schritt für Schritt wie du ein normales Ziel in ein SMARTES Ziel verwandeln kannst.<br></p><div style=\"text-align: center;\"><img src=\"../../upload/files/2017103055.jpg		\" style=\"width: 538px; float: none;\"></div><p><br></p>                                                                                                                                                                                                                                                                                                ', '2017-10-30', '20171030152254522', 'Persönlichkeitsbildung', '', '', 287, '', 0, '', 'Bring deine Ziele auf den Punkt!', 0, '1514897215');
