INSERT INTO `content` VALUES (150, 'Test', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: center; line-height: 1;\"><span style=\"background-color: inherit; color: rgb(0, 0, 0); text-align: justify;\">Oftmals ist es gar nicht so leicht, genau zu beschreiben was man wirklich gut kann und was so gar nicht. Auch für Stärken gibt es Fachbegriffe, die man kennen sollte.</span></p><p style=\"line-height: 1.2; text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: inherit;\">Egal ob für deine Ausbildungs-, Studien- oder Arbeitsauswahl - es ist wichtig, dass du dir deiner Talente bewusst bist. Du kannst dann nicht nur besser nach passenden Jobs umsehen, sondern auch besser Personen von dir (z.B. bei Bewerbungsgesprächen) überzeugen. </span></span></p><p style=\"line-height: 1.2; text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: inherit;\">Damit du dir einen Eindruck einiger Stärken und deren Eigenschaften verschaffen kannst, haben wir dir Links zu Videos bereitgestellt, die dir diese anhand von Beispielen beschreiben. Klick\' dich durch und überlege dir, welche der genannten Stärken dich beschreiben!</span></span></p><p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: inherit;\"><br></span><a href=\"http://www.planet-beruf.de/schuelerinnen/meine-talente/interessen-staerken/weitere-beitraege-interessen-staerken/videos-staerken-leicht-erklaert/ \" target=\"_blank\">Stärken in Videos leicht erklärt!</a></span></p><img src=\"../../upload/files/153person.jpeg&#9;&#9;\" style=\"width: 537px;\"><div style=\"text-align: center;\"><br></div><p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"background-color: inherit;\"><br></span></span></p><br>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ', NULL, '20170707083023511', 'Persönlichkeitsbildung', '', '', 139, 'Lösung hochladen', 1, '																								<div>Lieber Mentor</div><div><br></div><div>ein wichtiger Bestandteil der Arbeitswelt</div><div>ist der Umgang mit z.B. MS-Office. In diesen zwei Aufgaben sollen die Schüler</div><div>1) eine Tabelle formatieren und&nbsp;</div><div>2) eine kurze Power-Point rund um das Thema</div><div>Mentoring erstellen.</div><div>Geben Sie bitte hierzu Feedback. Für die Excel Aufgabe gibt es eine&nbsp;<a href=\"https://myfinancecoach.box.com/s/70i24gtda456831wx5xuaxs8rb4385up\" target=\"_blank\">https://myfinancecoach.box.com/s/70i24gtda456831wx5xuaxs8rb4385up</a></div><div><div>Auflösung.Achten Sie bei der Poiwer Point ob die</div><div>Answeisungen auch gefolgt worden sind</div></div>                                                                                                                        ', '', 1, NULL);
INSERT INTO `content` VALUES (151, 'Informieren 1b - Stärken und Fähigkeiten - Fachausdrücke verständlich erklärt', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">Ähnlich wie unser Aussehen sind auch unsere Stärken und Schwächen von Mensch zu Mensch sehr unterschiedlich. Mathegenie, Physikexperte, Sprachenguru oder Künstler! <br></p><p style=\"text-align: justify;\">Auf der Seite von \"Planet Beruf\" findest du eine ganze Reihe von interessanten Videos, die einige Stärken anschaulich beschreiben. </p><p style=\"text-align: justify;\">Klick\' dich durch, sieh dir die Videos an und überlege dir, welche Talente du persönlich mitbringst und welche&nbsp;Fähigkeiten auf dich zutreffen. Wage dich danach an den Test von E1!<br></p><p style=\"text-align: justify;\"><a href=\"http://www.planet-beruf.de/schuelerinnen/meine-talente/interessen-staerken/weitere-beitraege-interessen-staerken/videos-staerken-leicht-erklaert/\" target=\"_blank\">Stärken in Videoclips erklärt!</a></p><p style=\"text-align: center;\"><br></p>                                                                                                                                                <div style=\"text-align: center;\"><img style=\"width: 538px;\" src=\"../../upload/files/2017103068.jpg		\"></div>                                                                                                ', NULL, '2017100616111180', 'Persönlichkeitsbildung', '', '', 211, '', 0, '', 'Welche Fähigkeiten und Stärken zeichnen dich aus?', 0, '1515668511');
INSERT INTO `content` VALUES (153, 'Informieren 1 - Management von Einnahmen und Ausgaben', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\">W</span></font><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\">arum ist es sinnvoll mit Geld zu planen?<br>Brauchst du alles was du kaufst?<br>Wie behält man alle Einnahmen und Ausgaben im Blick?</font></span></font></span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\">Wenn man über den richtigen Umgang mit Geld Bescheid weiß, kann man unnötige Schulden vermeiden. Dafür gibt es einige Tipps, die - gewusst wie - ganz leicht umsetzbar sind.<br></font></span></font></span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\"><font style=\"\"><font style=\"\">Und wer seine Finanzen im Griff hat, kann sich mit hoher Wahrscheinlichkeit darauf freuen, eines Tages die schönen Dinge, Wünsche und Träume zu erfüllen!<br></font></font><br></font></span></font></span><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><a href=\"http://www.wirtschaftswerkstatt.de/Content/93/Ratgeber/Schritt_fuer_Schritt/Finanzen_So_gehst_du_richtig_mit_Geld_um.html\" target=\"_blank\">Klick</a><font style=\"\"> dich rein um mehr zu erfahren.</font></span></font></span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\"><br></font></span></font></span></p><img style=\"width: 538px; float: none;\" src=\"../../upload/files/2017112440.jpg		\"><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\"><br></font></span></font></span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><font style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"\"><br></font></span></font></span></p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ', '2017-10-23', '20171023131159828', 'Ökonomische Grundbildung', '', '', 130, '', 1, '', 'Wie gut weißt du über deine Finanzen Bescheid?', 1, NULL);
INSERT INTO `content` VALUES (154, 'Informieren 3 - Digital natives', 'Informieren', 'Video', '                                                                                                                                                                        <p>                                                                                                                                                                                                                                                </p><p></p><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Medien und Kommunikation, das gehört zusammen! Kommunikation bedeutet: Austausch oder Übertragung von Information.&nbsp; Denkt man genauer darüber nach, geht es bei allen Medienformen um Kommunikation, denn es werden dabei in bestimmter Weise immer Informationen vermittelt.</span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Entweder bekommst du etwas mitgeteilt, indem du zum Beispiel etwas liest oder ein Video ansiehst. Natürlich kannst du auch selbst etwas übermitteln, wenn du deinen Freunden Fotos zeigst, per Messenger eine Nachricht schickst oder in sozialen Netzwerken etwas postest.</span></div><div style=\"text-align: justify;\"><br></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Und wie bei einem direkten Gespräch gibt es auch bei der digitalen Kommunikation einiges zu beachten. Dieses Video erklärt dir, was ganz besonders wichtig ist, ganz nach dem Motto: <span style=\"font-weight: bold;\">Have you ever heard about Netiquette?</span></span></div><span style=\"color: rgb(0, 0, 0);\"><br></span><br><p></p>                                                                                                                                                                        ', NULL, '20170720084234138', 'Medienbildung', 'https://www.youtube.com/watch?v=80uRE972uQ0', 'Link', 296, '', 0, '', 'Have you ever heard about Netiquette?', 0, '1512643306');
INSERT INTO `content` VALUES (156, 'Informieren 3a - Lerntipps!', 'Informieren', 'Video', '                                                                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: justify;\">                                                                        Weiter geht\'s mit spannenden Lerntipps! Lernen ist eine wichtige Arbeitsmethode, die dir jeden Tag und vor allem in der Schule, bei den Hausaufgaben oder bei der Vorbereitung von Prüfungen begegnet! Keiner kommt darum herum, es kann daher schon einmal passieren, dass man sich fragt: Warum will der Lernstoff einfach nicht in meinen Kopf?</p><p style=\"text-align: justify;\">Auch in der Freizeit oder später im Beruf kann es sehr hilfreich sein, wenn man sich vieles merken kann: Geburtstage von Familie und Freunden, die Einkaufsliste oder die ToDo-Liste im Job! Lernen spielt jeden Tag eine Rolle! Was kann man also tun, damit das Gehirn fit bleibt, das Lernen leichter fällt und vor allem mehr Spaß macht?</p><p></p><div style=\"text-align: justify;\">Auf diese Frage hat The SimpleClub die passende Antwort und noch dazu ist sie ganz leicht umzusetzen. Lass dich überraschen und sieh dir in diesem Video an, was die beiden Jungs herausgefunden haben!<br></div><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><img style=\"width: 538px;\" src=\"../../upload/files/2017111427.jpg&#9;&#9;\"></div><div style=\"text-align: justify;\"><br></div><div style=\"text-align: justify;\"><br></div><br>                                                                                                <p></p>                                                                                                                                                                                                                                                                                                                                                                                                ', '2017-10-20', '20171020144124502', 'Arbeitsmethoden', 'https://www.youtube.com/watch?v=yxRlWHBor_s', 'Link', 157, '', 0, '', 'Was hat eigentlich Ernährung mit dem Thema Lernen zu tun?', 0, '1513871247');
INSERT INTO `content` VALUES (159, 'Erarbeiten 3 - SMART Ziele formulieren', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                <div style=\"text-align: justify;\">Jetzt bist du dran - versuche deinen Ziele durch konkrete Formulierungen einen Schritt näher zu kommen. </div><div style=\"text-align: justify;\"><br></div><div style=\"text-align: justify;\">Hierfür solltest du dir zunächst zwei Ziele herauspicken, die du bisher noch nicht in die Tat umsetzen konntest, die aber eigentlich gar nicht so schwer umsetzbar wären. Lernst du rechtzeitig genug? Machst du so viel Sport wie du möchtest?<br></div><div style=\"text-align: justify;\"><br></div><div style=\"text-align: justify;\">Nimm dir dazu am besten das Infoblatt (I3b) zur Hilfe und gehe schrittweise vor, um alle SMART-Regeln in einem Satz zu berücksichtigen. Lade das Arbeitsblatt im Anschluss hoch. Wir werden die deine Ergebnisse mit dir besprechen.<br></div><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><img src=\"../../upload/files/2017103052.jpg		\" style=\"width: 543.5px; height: 362.761px;\"></div><div style=\"text-align: center;\"><br></div>                                                                                                                                                                                                ', '2017-10-30', '20171030171152530', 'Persönlichkeitsbildung', '', '', 302, 'Lösung hochladen', 0, '', 'Erfolgreich Ziele erreichen!', 0, '1514897271');
