INSERT INTO `content` VALUES (214, 'Erarbeiten 1 - Zukunft', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                <p>Jeder Mensch hat seine ganz persönlichen Wünsche und Träume. Und manchmal sind das auch Wünsche für die sehr lange gespart werden muss. Dafür ist die Freude dann umso größer! </p><p>Hast du Träume oder etwas, das du dir ganz besonders für die Zukunft wünschst? </p><p>Erzähle ein bisschen über dich..</p><p style=\"text-align: center;\"><br></p><div style=\"text-align: center;\"><img style=\"width: 538px; float: none;\" src=\"../../upload/files/2017111489.jpg		\"></div><p><br></p><p>                        </p>                                                                                                                                                                                                ', '2017-11-14', '20171114134130356', 'Ökonomische Grundbildung', '', '', 215, 'Bearbeitung über Textfeld', 0, '', 'Deine Wünsche und Träume', 1, '1512121726');
