INSERT INTO `content` VALUES (287, 'Erarbeiten 5 - YouTube', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                        <p style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Erinnerst du dich an Anna, die YouTuberin und daran was sie über ihre Arbeit gesagt hat? Könntest du dir ebenfalls vorstellen als YouTuber/in aktiv zu sein oder hast du vielleicht schon mal selbst ein Video aufgenommen?</span></p><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Wie denkst du über die Vor- und Nachteile von Youtube? Wie schätzt du diese ein?</span></div><p style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\">Schreibe deine Meinung und Gedanken hier im Textfeld auf und begründe diese!<br></span></p>                                                                                                                                                                        ', '2017-10-26', '20171026095635765', 'Medienbildung', '', '', 324, 'Bearbeitung über Textfeld', 0, '', 'Was sagst du dazu?', 0, '1512656411');
