INSERT INTO `content` VALUES (300, 'Informieren 6 - Gestaltung von Präsentationen', 'Informieren', 'Video', '                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">Toll, wie du das mit den Tabellen geschafft hast! Deshalb geht es jetzt auch schon mit dem nächsten Thema weiter. Und zwar mit den Programmen zur Erstellung von Präsentationen!</p><p style=\"text-align: justify;\">Vielleicht hast du ja schon einmal eine Präsentation für ein Referat oder ein Projekt in der Schule erstellt und deswegen schon ein bisschen Erfahrung! Auf jeden Fall wird diese Aufgabe immer wieder auf dich zukommen, denn Präsentationen können für alle möglichen Anlässe sinnvoll und nützlich sein!</p><p style=\"text-align: justify;\">Klick das Video an und du erfährst die wichtigsten Grundlagen zur Erstellung und Gestaltung von Präsentationen!<br></p>                                                                                                                                                                                                                                                                                                                        ', '2017-10-27', '20171027111014549', 'Medienbildung', 'https://youtu.be/p7fcpGB5q4g', 'Link', 301, '', 0, '', 'Präsentationen erstellen und gestalten', 1, NULL);
