INSERT INTO `content` VALUES (442, 'Informieren 6 - Präsentationen gestalten', 'Informieren', 'Video', '                                                                        <div style=\"text-align: left;\">                        <span style=\"color: rgb(0, 0, 0);\">Vielleicht hast du schon mal eine Präsentation für ein Referat oder ein Projekt in der Schule erstellt und bereits erste Erfahrung mit dem ein oder anderen Programm sammeln können! Auch nach der Schule sind Präsentationen erstellen und diese vortragen häufig ein Thema und für sehr viele Berufe relevant.<br><br>Um einen guten Einblick in die Grundzüge solcher Programme zu erhalten, sieh\' dir das Video an und erfahre die wichtigsten Grundlagen zur Erstellung und Gestaltung von Präsentationen! </span><br></div>                                                                                                ', '2017-11-22', '20171122095039217', 'Medienbildung', 'https://youtu.be/p7fcpGB5q4g', 'Link', 420, '', 0, '', 'Präsentationen erstellen leicht gemacht!', 0, '1512649690');
INSERT INTO `content` VALUES (443, 'Zusatzinfo: Über Geld sprechen', 'Informieren', 'Video', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); text-align: left; font-size: 14px;\">Wie ist das eigentlich mit dem Geld, das man verdient? <br>Für welche Arbeit bekommt man wie viel Geld? </span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); text-align: left; font-size: 14px;\"><br></span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); text-align: left; font-size: 14px;\">Ein Beispiel: Patrick und Ben arbeiten in derselben Disko. Patrick ist der Betriebsleiter\n und Ben jobbt dort am Wochenende als Flaschensammler, weil er als \nausgebildeter Zahntechniker zu wenig verdient, um sich nebenher noch etwas mehr \nleisten zu können. Allerdings verdient Patrick pro Monat fast das Vierfache von Ben.\n </span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); text-align: left; font-size: 14px;\"><br></span></div><div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0); text-align: left; font-size: 14px;\">Was begründet diesen großen Unterschied?</span></div><div style=\"text-align: left;\"><font style=\"font-size: 18px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"font-size: 12px;\"><br></font></span></span></font></div><div style=\"text-align: center;\"></div><div style=\"text-align: center;\"><font style=\"font-size: 18px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"font-size: 12px;\"><br></font></span></span></font></div><div style=\"text-align: center;\"><img src=\"../../upload/files/96okono.png		\" style=\"width: 509px;\"></div><div style=\"text-align: center;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><font style=\"font-size: 18px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-weight: bold;\"></span></span></font>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ', NULL, '20171006140317470', 'Ökonomische Grundbildung', 'https://www.planet-schule.de/wissenspool/fair-pay/inhalt/sendung.html', 'Link', 451, '', 1, '																																																																		<div style=\"text-align: center;\">Das ist ein Test für eine Mentor-Beschreibung.</div>                                                                                                                                                                                                                                                                        ', 'Fair pay - Warum verdienst du mehr als ich?', 0, '1512641704');
