INSERT INTO `content_history` VALUES (295, 'Haushaltsplanung', 'Informieren', 'Lesestoff', '                                                                                                                                                                                                                                                                                                <p style=\"line-height: 1.2;\"></p><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Doch jetzt bist du auch dafür verantwortlich, vieles selbst zu organisieren und die Finanzen in den Griff zu bekommen.</span></div><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Hier erhältst du einige Tipps: <span style=\"color: rgb(0, 0, 0);\">h<a style=\"background-color: rgb(255, 255, 255);\" href=\"http://www.fit-for-money.de/downloads/erste_Wohnung.pdf&nbsp;\" target=\"_blank\">ttp://www.fit-for-money.de/downloads/erste_Wohnung.pdf <br></a></span></span></div><div style=\"text-align: center; line-height: 1.4;\"></div><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\"><br></span></span></div><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">&nbsp;</span></span></div><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">&nbsp;</span></span></div><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><br></span></div><p style=\"line-height: 1.4;\"></p><p style=\"line-height: 1.4;\"></p><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><br></span></div><div style=\"text-align: justify; line-height: 1.4;\"><br></div>                        <p></p>                                                                                                                                                                                                                                                                                                ', NULL, '20170720092638836', 'Ökonomische Grundbildung', '', '', 417, '', 0, '', 'Wenn du deinen Schulabschluss in der Tasche hast, stellt sich die Frage ob du noch zu Hause wohnen bleibst oder eventuell wegziehen musst.', 1, NULL);
INSERT INTO `content_history` VALUES (298, 'Erarbeiten 1 - Umgang mit neuen Medien', 'Erarbeiten', 'Aufgabe', '                                                                                                                                                                                                                                                                                                                                                                        <p>Los geht\'s mit dem Aufgabenteil im Baustein Medienbildung. Sei gespannt auf viele abwechslungsreiche Herausforderungen!<br><br><span style=\"font-weight: bold;\">Beginnen wir mit einem Quiz:</span></p><p>Im Informationsteil konntest du ja schon vieles zum Thema Medien, Datenschutz &amp; Co. herausfinden. Checke dein Wissen mit diesem Quiz und werde zum Datenexperten und zur Datenexpertin! Lade die Auswertung anschließend hoch und bespreche die Ergebnisse mit deinem Mentor.<br></p><p></p><p style=\"text-align: left; line-height: 1.2;\"><span style=\"font-weight: bold;\"><a href=\" http://www.klicksafe.de/qz/quiz03/_project/\" target=\"_blank\">Teste dein Medienwissen</a></span><span style=\"color: rgb(0, 0, 0); background-color: inherit; text-align: justify; font-size: 14px;\"><br></span></p><p style=\"text-align: center; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0); background-color: inherit; text-align: justify; font-size: 14px;\"><br></span></p><div style=\"text-align: center;\"><img src=\"../../upload/files/29medienbildung1.jpeg		\" style=\"width: 882.5px; height: 567.556px;\"></div><p style=\"text-align: justify; line-height: 1.2;\"><br></p>                                                                                                                                                                                                                                                                                                                                                                                                                        <p></p>                                                                                                                                                                                                                                                                                                                                                                        ', NULL, '2017072008520417', 'Medienbildung', '', '', 126, 'Bearbeitung über Textfeld', 1, '																								<p>Lieber Mentor,</p><p>zum Thema Datenschutz wird sich dein Mentee durch ein Medienquiz klicken und die Antwort auf die Frage \"Bist du ein Datenprofi im Internet\" erhalten. Es geht beispielsweise um sichere Passwörter, den Umgang mit personenbezogenen Daten und sozialen Netzwerken. </p><p>Die Quiz-Auswertung zeigt dir, wo dein Mentee eventuell noch Wissenslücken hat und bei welchen Themen sie/er bereits Experte/Expertin ist. Bespreche die auffälligen Ergebnisse mit deinem Mentee und klärt ggf. offene Fragen.</p><p>Viel Spaß dabei!</p><p>Dein My Mentor-Team<br></p><p><br></p>                                                                                                ', 'Wie gut kennst du dich mit Medien aus?', 0, '1512638615');
