insert  into `content`(`cid`,`title`,`category`,`contenttype`,`description`,`contentdate`,`randomnumber`,`parentcategory`,`videolink`,`videotype`,`orderid`,`lessontype`,`allowchat`,`mentordescription`,`subtitle`,`deleted`,`randomtwo`) values (110,'hgfvukfkuf','Informieren','Aufgabe','                                                                        ','2017-11-22','20171122141418456','Arbeitsmethoden','','',441,'Bearbeitung über Textfeld',0,'						dfhgshsghgjsrjrjjjjjjjjjjjjjjjjjjjjjj                                                ','jägöiuföi',1,'1511997325'),(114,'Erarbeiten 2 - Planen und Sparen','Erarbeiten','Aufgabe','                                                                                                                                                                                                                                                                                                                                                                                                                                                <div style=\"line-height: 1.4; text-align: justify;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Stellt dir vor, du möchtet dein Geld anlegen. Da du nicht sicher bist, wie man dies am besten macht, hast du für einen Termin beim Berater vereinbart.&nbsp;</span></div>                        <div style=\"text-align: justify;\"><span style=\"color: rgb(0, 0, 0);\"><div style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\">Beantworte die Fragen auf dem Arbeitsblatt \"Beratungsgespräch\" und vereinbare einen Termin mit deinem <span style=\"color: rgb(0, 0, 0);\">Mentor, er/sie wird die Rolle des Beraters übernehmen.</span></span></div></span></div><span style=\"color: rgb(0, 0, 0);\"><div style=\"text-align: justify; line-height: 1.2;\"><br></div></span><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><img src=\"../../upload/files/58okono5.jpeg		\" style=\"width: 492.3px; height: 328.2px;\"></div><div style=\"text-align: center;\"><br></div><span style=\"color: rgb(0, 0, 0);\"><div style=\"text-align: justify; line-height: 1.2;\"><br></div></span>                                                                                                                                                                                                                                                                                                                                                                                                                                                ','','20170925100157615','Ökonomische Grundbildung','','',424,'Lösung hochladen',0,'																																																																																										<p><span style=\"color: rgb(0, 0, 0);\">Liebe/r Mentor/in, </span></p><p><span style=\"color: rgb(0, 0, 0);\">es kommt shcon öfters vor, dass für ein Sparziel nicht die geeignete Anlageform ausgesucht wird. </span></p><p><span style=\"color: rgb(0, 0, 0);\">Ihre Aufgabe ist in die Rolle des Beraters zu schlüpfen und einen Beratungsgespräch zu simulieren. Ihr Mentee hat sich om Vorfeld schon Gedanken über sein/ihr Sparziel gemacht (siehe Arbeitsblatt Beratunsgespräch)&nbsp; und nun möchte er/sie erfahren welche Anlageform zur Verfügung steht. <a href=\"https://myfinancecoach.box.com/s/qtd34oif2ahz8x81je2sijk9o5sld8s2\" target=\"_blank\">Hier</a> finden Sie eine Übersicht der Anlageformen und die Anlagekriterien (Risiko-Rendite-Liquidität). <br></span></p><p><span style=\"color: rgb(0, 0, 0);\"><span style=\"text-decoration: underline;\">Beratungsgespräch:</span></span></p><p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\">Guten Tag, wie kann ich dir helfen?</span><br></span></p><p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\">• Wofür möchtest du sparen?<br>• Welche Summe willst du ansparen?<br>• Wann brauchst du frühestens dein Geld wieder?</span> (Ihr Mentee hat sich bereits über diese Fragen Gedanken gemacht)</span></p><p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\">• und was ist dein wichtigstes Anlagekriterium?</span><br></span></p><p><span style=\"color: rgb(0, 0, 0);\">–– Der Mentee nennt seine Präferenz zum Thema Rendite, Risiko und Liquidität.<br>––Sie fassen zusammen und empfehlen dann eine wenig geeignete Anlageform<br>(z. B. eine Lebensversicherung, wenn der Schüler auf sein Studium sparen möchte).<br>• Berater: Was meinst du hierzu?<br>––Ihr Mentee äußert sich, wie dies in Bezug auf seine Interessen und Kriterien passt.<br></span></p><p><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\">• Berater: Also sind wir uns einig?</span><br></span></p><p><span style=\"color: rgb(0, 0, 0);\">Sie können folgendes mit Ihrem Mentee diskutieren:<br>• Würdet ihr jetzt sofort zusagen?<br>• Was könnte man hier anders machen? Welche Fragen könnte man dem Berater noch<br>stellen?<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;––Welche Alternativen gäbe es?<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;––Was passiert, wenn sich meine Lebensumstände ändern?<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;––Welche Bearbeitungsgebühren oder sonstigen Kosten fallen an?<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;––Können wir mein Sparziel mit der Entscheidung zu Rendite, Liquidität und Risiko sowie Ihre Empfehlung dazu bitte schriftlich festhalten?<br><br></span></p><p><span style=\"color: rgb(0, 0, 0);\">Sprechen Sie im Anschluss nochmal über die Situation, wie war es für den Mentee, wie kann man sich auf ein solches Beratungsgespräch vorbereiten?<br></span><br></p>                                                                                                                                                                                                                                                                                                                                                                        ','Risiko, Rendite, Liquidität. Wie war das nochmal?',1,NULL),(118,'Erarbeiten 3 -  Und wie lernst du?','Erarbeiten','Aufgabe','                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: left;\">Der Lerntypentest kann dir in Zukunft sicher helfen, deine bevorzugte Lernmethode besser einzuschätzen. Vielleicht kanntest du aber auch schon vorher ein paar Tricks, die du beim Mathe pauken oder Vokabeln lernen immer wieder anwendest. Es kann auch sein, dass du das gar nicht bewusst machst, sondern automatisch und unterbewusst darauf zurückgreifst.<br></p><p style=\"text-align: justify;\">Die nächste Aufgabe lautet deshalb: Überlege, wie dir Lernen am leichtesten fällt, selbst wenn du dir darüber noch nie so wirklich Gedanken gemacht hast. </p><p style=\"text-align: justify;\">Welche besonderen, persönlichen, komischen oder lustigen Tipps und Eigenheiten hast du? Ob du beim Lernen gerne auf dem Kopf stehst, hunderte von Notizzetteln bekritzelst oder den Erdkundestoff singst - ganz egal, schreibe, zeichne, bastele oder male es auf, mach‘ ein Foto davon und lade es hier hoch! <br></p><p style=\"text-align: center;\"><br></p><div style=\"text-align: center;\"></div><p style=\"text-align: center;\"><br></p><div style=\"text-align: center;\"><img style=\"width: 539px;\" src=\"../../upload/files/2018011173.jpeg		\"></div><p><br></p><div style=\"text-align: center;\"></div><p style=\"text-align: center;\"><br>                        </p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                ','2017-10-24','2017102416000887','Arbeitsmethoden','','',120,'Lösung hochladen',0,'','Jetzt wird es kreativ. Zeig wie du lernst!',0,'1512034215'),(119,'Erarbeiten 4 - Meine Schulwoche','Erarbeiten','Aufgabe','                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">                        Nachdem du bereits deine Kreativität unter Beweis stellen konntest, geht es jetzt wieder um Struktur und zwar um die Analyse einer deiner ganz persönlichen Schulwochen!<br></p><p style=\"text-align: justify;\">Wie du weißt, ist es wichtig und praktisch seine Zeit gut einzuteilen. Denn ein Tag hat nun mal nur 24 Stunden und es gibt meistens sehr viel zu erledigen und zu tun. </p><p style=\"text-align: justify;\">Mit einem Plan gerät man nicht unter Druck, man vermeidet stressige Situationen, wenn die Zeit bis zur nächsten Prüfung mal wieder viel zu schnell vergeht und man außerdem noch ein Referat vorbereiten muss. Außerdem bleibt am Ende mehr Zeit für Hobbys und andere schöne Dinge!<br></p><p style=\"text-align: justify;\"><span style=\"font-weight: bold;\">Nun zur Analyse deiner Schulwoche. Deine Aufgabe lautet:</span><br></p><p style=\"text-align: justify;\">1. Unten findest du eine Vorlage für deinen Wochenplan (\"Meine Wochenanalyse\") sowie einen beispielhaften Wochenplan!<br></p><p style=\"text-align: justify;\">2. Ziel ist es, dich selbst eine Woche lang zu beobachten und zu notieren, wie deine Zeiteinteilung aussieht.<br></p><p style=\"text-align: justify;\">3.&nbsp;&nbsp; &nbsp;Überlege dir, welche Woche du analysieren möchtest (am besten wählst du ein nicht zu weit entferntes Datum und wenn möglich eine Woche aus, in der du einiges zu tun hast, zum Beispiel Prüfungen und Referate, Geburtstage, ein Konzert, Turnier oder ähnliches). <br></p><p></p><div style=\"text-align: justify;\">4.&nbsp;&nbsp;&nbsp; Dein Plan besteht aus einer Wochenübersicht und verschiedenen Feldern mit bunten Balken. Im Beispiel (\" Beispiel für die Wochenanalyse\") siehst du, welchen Aktivitäten eine jeweilige Farbe zugeordnet ist. Behalte diese Einteilung bei. Im Feld „SONSTIGES“ hast du die Möglichkeit eine oder mehrere weitere Aktivitäten hinzuzufügen.</div><div style=\"text-align: justify;\"><br></div><div style=\"text-align: justify;\">5.&nbsp;&nbsp; &nbsp;Beginne an einem Montag deiner ausgewählten Woche und beobachte dein Zeitmanagement. Markiere in deinem Wochenplan die jeweiligen Zeiträume in der entsprechenden Farbe (z. B. Blau für den Zeitraum in der Schule) und notiere dazu, um was es sich genau handelt.<br></div><p></p><p style=\"text-align: justify;\">6.&nbsp;&nbsp; &nbsp;Lade deinen Plan am Ende der Woche hoch, zum Beispiel indem du diesen abfotografierst oder einscannst.<br></p><p style=\"text-align: justify;\">7.&nbsp;&nbsp; &nbsp;Da es sich um eine Analyse handelt, geht es letztlich auch darum auszuwerten für was du wieviel Zeit verwendet. Berechne hierfür die Summe der Stunden für jedes der farbigen Felder und notiere dein Ergebnis. </p><p style=\"text-align: justify;\">Hast du mit diesem Ergebnis gerechnet? Wirst du in Zukunft an deinem Zeitmanagement etwas ändern? Für was hättest du eigentlich gerne mehr Zeit und wo könntest du möglicherweise Zeit einsparen? Beantworte diese Fragen und reflektiere deinen Wochenplan. Nutze hierzu das Textfeld sowie die Uploadfunktion!<br></p><p></p><div style=\"text-align: justify;\">Hier findest du das Beispiel sowie die Vorlage für <span style=\"font-weight: bold;\">deinen persönlichen Wochenplan zum Ausdrucken</span>! Lade deinen ausgewerteten Plan nach der Analyse hier hoch.<br></div><br><div style=\"text-align: center;\"><br></div><div style=\"text-align: center;\"><img src=\"../../upload/files/2017102450.jpg		\" style=\"width: 538px;\"></div>                        <p></p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ','2017-10-24','20171024160904825','Arbeitsmethoden','','',414,'Lösung hochladen',0,'','Dein Zeitmanagement:  Analysiere deine Schulwoche!',0,'1512632068'),(121,'Nun bist du sicher gut für den TEST vorbereitet. ','Test','Quiz','<br>','','20170718090436585','Persönlichkeitsbildung','','',122,NULL,NULL,NULL,NULL,0,NULL),(125,'E2 Ausbildung oder Schule? ALT','Erarbeiten','Aufgabe','                                                                                                                                                <p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\">Überlegst du zu <font style=\"\"><span style=\"font-weight: bold;\">studieren</span></font>? Bei diesem Test kannst du erfahren was zu dir passt und in welche Richtung es gehen kann:</span><font style=\"\"> &nbsp;<a href=\"http://www.was-studiere-ich.de/\" target=\"_blank\">http://www.was-studiere-ich.de/</a></font></span></p><p style=\"text-align: justify;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\">oder eher eine <font style=\"\"><span style=\"font-weight: bold;\">Ausbildung</span></font> zu machen? Finde heraus welche Berufe zu dir passen:</span> <font style=\"\"><a href=\"https://set.arbeitsagentur.de/teaser\" target=\"_blank\" style=\"\">https://set.arbeitsagentur.de/teaser</a></font></span><br>                        </p>                                                                                                                                                ','','20171010124728138','Berufsorientierung','','',185,'Lösung hochladen',1,'						Das ist die Mentorbeschreibung E2                        ','In Deutschland gibt es alleine mehr als 5000 Studien- und Ausbildungsmöglichkeiten',1,NULL),(126,'(I3) Projektplanung II','Informieren','Video','                                                                                                                                                                                                                                                                                                <div style=\"text-align: justify;\" align=\"center\"><font style=\"text-align: left; font-size: 14px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\">Bestimmt hast du selbst schon einmal ein Projekt&nbsp;organisiert oder bei der Umsetzung einer Idee mitgemacht. Vielleicht hast du dabei schon festgestellt, dass das manchmal gar nicht so leicht ist. Ein Projekt zu organisieren und durchzuführen erfordert eine gute Planung und Aufgabenverteilung. In folgendem Video erfährst du was es alles zu beachten gibt!</span></span>&nbsp;&nbsp;</font><span style=\"text-align: left;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></div>                                                                                                                                                                                                                                                ','','20170718094815659','Arbeitsmethoden','<iframe width=\"854\" height=\"480\" src=\"https://www.youtube.com/embed/C0O4WJudmo0\" frameborder=\"0\" allowfullscreen></iframe>','Link',149,'',0,'','Projekte planen - Und wie geht man dabei am besten vor?',1,NULL),(129,'(I1) - Termine','Informieren','Lesestoff','<br><div style=\"text-align: center;\"><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Digitale Programme...was ist denn damit gemeint? </span></span></font></span></span></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Und was gibt es da für welche? </span></span></font></span></span></span>Und vor allem...warum fangen wir damit an? Fragen über Fragen..<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Die Arbeit am PC ist im Laufe der Zeit immer wichtiger geworden und zählt deshalb mit zu den bedeutendsten Arbeitsmethoden!</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"> Ob in der Schule, in der Ausbildung, im Studium oder im Arbeitsleben, überall sind gewisse Kenntnisse im Umgang mit verschiedenen digitalen Programmen notwendig und nicht mehr wegzudenken. Außerdem sind PC-Kenntnisse nicht nur wichtig wenn man später mal im Büro arbeitet sondern zum Beispiel auch in vielen anderen Bereichen wie der Landwirtschaft, der Medizin oder in Handwerksbetrieben.<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Und weil du ja der eigentliche \"digital native\" und Experte auf diesem Gebiet bist, weißt du bestimmt sowieso schon super viel über die Welt der Computer! Manche Programme aber sind ganz besonders wichtig und man braucht sie immer wieder: Zum Beispiel Microsoft Office Word zum Erstellen von Texten, Power Point zur Gestaltung von Präsentationen und Excel zur Arbeit mit Zahlen.</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">In diesem Teil geht es nun erstmal um das Programm Word und das Erstellen einer PDF-Datei. So kannst du erst einen Text schreiben und den dann danach in eine Datei umwandeln, die keiner mehr so leicht verändern kann! Das ist manchmal total praktisch und du kannst die Funktion auch gut brauchen wenn eine Aufgabe hier bei My Mentor darin besteht, eine von dir erstellte Datei hochzuladen.<br></span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\">Wie es geht kannst du ganz einfach in dieser Kurzanleitung nachlesen! Wenn du dich schon damit auskennst, kannst du den Teil auch überspringen und die Anleitung zum Nachlesen im Kopf behalten!</span></span></font></span></span></span></p><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span></font></span></span></span></p></div><img style=\"width: 432.417px; height: 432.417px;\" src=\"../../upload/files/79balloon-1345879_1920.png		\"><div style=\"text-align: center;\"><p style=\"text-align: left;\"><span style=\"color: rgb(0, 0, 0);\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"font-size: 18px;\"><font style=\"font-size: 14px;\"><span style=\"background-color: inherit;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></span></font></span></span></span></p></div>                                                                                                                                                                                                                                                                                                                                                ','','20171009144610332','Arbeitsmethoden','','',329,'Lösung hochladen',0,'','Hey! Schön, dass du hier bist! Und los geht\'s mit dem Baustein Arbeitsmethoden! Lass uns gleich mit dem ersten spannenden Thema starten: Digitale Programme',1,NULL),(130,'Konfliktlösung: dein Verhalten','Informieren','Aufgabe','                                                                                                                                                                                                                        <p style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Thema Konfliktlösung: Konflikten ist jeder ausgesetzt, dann wenn es gerade mit den Eltern stressig oder weil die Freunde nicht wie erwartet dich unterstützt haben. Doch diese zu umgehen ist nicht immer die beste Alternative.</span></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"font-size: 14px;\"><span style=\"color: rgb(0, 0, 0);\"> In diesem </span><span style=\"font-weight: bold;\"><font style=\"\"><a href=\"http://www.jugend-und-bildung.de/webcom/show_article.php/_c-946/_nr-9/i.html\" target=\"_blank\">Test</a></font></span><span style=\"color: rgb(0, 0, 0);\"><span style=\"font-weight: bold;\"><font style=\"\"> </font></span>kannst du rausfinden wie du in bestimmten Situationen reagieren würdest. Futter zum Nachdenken…und durchatmen!</span></span></p><p style=\"line-height: 1.2;\"></p><div style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><span style=\"color: rgb(0, 0, 0);\"><br></span><p></p><p><span style=\"color: rgb(0, 0, 0);\"><br></span><span style=\"color: rgb(0, 0, 0);\"><br></span>                        </p>                                                                                                                                                                                                                        ','','20170718095226794','Arbeitsmethoden','','',268,'',0,'','Stärken aufzählen kann jeder - Warum ist eine Begründung essentiell?',1,NULL),(132,'Informieren 3c - Der Lerntypentest','Informieren','Lesestoff','                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        <p style=\"text-align: justify;\">Nachdem du nun schon einiges über Tricks und Kniffe für erfolgreiches Lernen gehört und gelesen hast, wollen wir nun deinen individuellen Lerntyp bestimmen. Der zeigt dir nämlich, wie du dir Lerninhalte und Wissen am besten einprägen kannst. Zum Beispiel, wenn du dich fragst, wie du dir die neuen Vokabeln aus dem Englischunterricht am besten merken kannst. <br></p><p style=\"text-align: justify;\">Vielleicht klappt es am besten, wenn du sie dir häufig aufschreibst oder sie dir einige Male anhörst oder du dir kleine Zettel an bestimmte Gegenstände klebst. Jeder hat seine eigenen Methoden, wie er letztlich am besten mit neuem Lernstoff umgeht.<br></p><p style=\"text-align: justify;\">Neben Mischtypen, kann man zwischen vier groben Lerntypen unterscheiden. Und zwar den auditiven, den visuellen, den motorischen und den kommunikativen Lerntyp. Mithilfe des Tests kannst du herausfinden, welcher Lerntyp am besten auf dich zutrifft. Gleichzeitig findest du Strategien und Tricks, wie du versuchen kannst, die nächsten Vorbereitungen auf Prüfungen zu verbessern. Außerdem ist es hilfreich, immer wieder einfach ein bisschen auszuprobieren, welche Lernmethoden für dich geeignet sind.</p><div style=\"text-align: justify;\">Über die Download-Funktion geht’s direkt zum Test. Drucke ihn dir am besten aus und kreuze die zutreffenden Antworten mit einem Stift an (oder PDF-Bearbeitung). Die Auflösung gibt es ebenfalls zum Herunterladen. Viel Erfolg dabei!&nbsp;</div><p style=\"text-align: center;\"></p><div style=\"text-align: center;\"><img style=\"width: 538px;\" src=\"../../upload/files/42study-2746004_1920.jpg		\"></div><p><br></p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ','2017-10-20','20171020150019281','Arbeitsmethoden','','',428,'',0,'','Welcher Lerntyp bist du?',0,'1513172263'),(133,'Kommunikation, Rhetorik und Konfliktlösung','Informieren','Lesestoff','                                                                                                                                                <p style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px;\">Hier geht es darum, wie man es schafft, gute Referate zu halten: </span></p><p style=\"text-align: justify; line-height: 1.2;\"><a style=\"font-size: medium;\" href=\"http://karrierebibel.de/referat-halten/\" target=\"_blank\">http://karrierebibel.de/referat-halten/</a><br></p>                                                                                                                                                ','','20170718095505162','Arbeitsmethoden','','',326,'',0,'','',1,NULL),(134,'(E1) Effizienter lernen','Erarbeiten','Aufgabe','                                                                                                                                                                                                                                                                                                                                                <p style=\"text-align: left; line-height: 1.4;\"><span style=\"color: rgb(0, 0, 0); font-size: 14px; text-align: justify;\">Hast du schon mal was von Lerntypen gehört? Ob Hören, Sehen oder das Sprechen mit anderen...jeder Mensch lernt gerne ein bisschen anders!</span></p><p style=\"text-align: justify; line-height: 1.4;\"><font style=\"font-size: 14px;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\">Wenn du weißt welcher Lerntyp du bist, wirst du dir in jedem Fall beim Einprägen von neuen Dingen sehr viel leichter tun! </span></span><span style=\"color: rgb(0, 0, 0);\">Es kann aber auch sein, dass für dich\n eine Mischung aus Lerntypen zutrifft! Mache deshalb den Test und probiere es in Zukunft einfach einmal aus, indem du darauf\n achtest wie du am besten Lernen kannst.</span></font></p><p style=\"text-align: justify; line-height: 1.4;\"><span style=\"color: rgb(107, 165, 74);\"><span style=\"color: rgb(0, 0, 0);\"><font style=\"font-size: 14px;\">Hier geht\'s zum Test: </font><br></span></span></p><div style=\"text-align: justify; line-height: 1.2;\"><span style=\"color: rgb(0, 0, 0);\"><br></span></div><span style=\"font-weight: bold; color: rgb(107, 165, 74);\"></span>                        <p></p>                                                                                                                                                                                                                                                                                                   